mapper.update:
UPDATE study
SET current_member = current_member + #{num}
WHERE study_no = #{studyNo}

mapper.select:
SELECT study_no, email, study_name, study_hashtag, study_thumbnail, TO_CHAR(study_start_date,'YYYY/MM/DD') study_start_date, TO_CHAR(study_end_date,'YYYY/MM/DD') study_end_date, current_member, max_member, TO_CHAR(recruitment_start_date,'YYYY/MM/DD') recruitment_start_date, TO_CHAR(recruitment_end_date,'YYYY/MM/DD') recruitment_end_date, studyroom_grade, study_type, study_interest FROM study
study_end_flag = '1' AND study_type = 'self' AND email = #{email}
ORDER BY study_no ASC

mapper.update:
UPDATE
	study
SET
	groupstudy_notice = #{groupStudyNotice}
WHERE
	study_no = #{studyNo}
