mapper.update:
UPDATE users
suspend_start_date = '', suspend_end_date = '', condition = 1
WHERE email = #{receiveReporter.email}

mapper.select:
SELECT COUNT(*) FROM ( SELECT r.receive_reporter_email, r.send_reporter_email, r.report_no, r.report_reason, u.nickname, r.report_date, u.condition FROM users u, report r WHERE u.email = r.receive_reporter_email AND answer_no is null AND comment_no is null AND board_no is null
AND r.receive_reporter_email LIKE '%'||#{search.searchKeyword}||'%'
) countTable
